executeclass.arg:
@echo off
set BASE_PATH=${INSTALL_PATH}
set JAVA_HOME=%BASE_PATH%\softwares\jdk1.6.0_41
set MAVEN_HOME=%BASE_PATH%\softwares\apache-maven-3.0.5
set CATALINA_HOME=%BASE_PATH%\softwares\apache-tomcat-7.0.37
set MAVEN_OPTS = -Xms256m -Xmx512m -XX:MaxPermSize=128m

set PATH=%PATH%;%JAVA_HOME%\bin;%MAVEN_HOME%\bin;%CATALINA_HOME%\bin

@echo on
cd %BASE_PATH%\workspace

mvn -version

#!/bin/sh export BASE_PATH=${INSTALL_PATH} export JAVA_HOME=$BASE_PATH/softwares/jdk1.6.0_41 export MAVEN_HOME=$BASE_PATH/softwares/apache-maven-3.0.5 export CATALINA_HOME=$BASE_PATH/softwares/apache-tomcat-7.0.37 export MAVEN_OPTS = -Xms256m -Xmx512m -XX:MaxPermSize=128m
export PATH=$PATH:$JAVA_HOME/bin:$MAVEN_HOME/bin:$CATALINA_HOME/bin cd $BASE_PATH/workspace
mvn -version
